member.example:
// Method 1: Sort children with separate service
[SortChildren(typeof(MyCustomSorter))]
public class MyCustomContainer : ContentItem
{
}

public class MyCustomSorter : IChildrenSorter
{
   // custom sorting implemenation
}

// Method 2: Parent content item implements sorting
[SortChildren(SortBy.CustomSorter)]
public class MySelfSortableContainer : ContentItem, IChildrenSorter
{
   // custom sorting implemenation
}

[N2.Details.EditableText("Heading", 80)]
public virtual string Heading { get; set; }

[N2.Details.EditableText("Published", 80)]
public virtual DateTime PublishedDate { get; set; }

[WithEditableTranslations(ContainerName = "SiteContainer")]
public class StartPage : ContentPage, ITranslator
{
	public string Translate(string key, string fallback = null)
	{
		return DetailCollections.GetTranslation(key) ?? fallback;
	}
	public IDictionary<string, string> GetTranslations()
	{
		return DetailCollections.GetTranslations();
	}
}

